package org.elsys.first;

public class Point {
	private int x;
	private int y;
	public Point(int i, int j) {
		// TODO Auto-generated constructor stub
		x=i;
		y=j;
	}
	public void setX(int num) {
		x = num;
	}
	public void setY(int num){
		y = num;
	}
	public int getX() {
		return x;
	}
	public int getY() {
		return y;
	}
	
}
